package com.arithmetic.solution;import java.util.Deque;import java.util.LinkedList;/** * @author Zhang Yu * @date 2022/3/3 10:09 PM * 栈做一个队列 */public class CQueue {    private Deque<Integer> firstDeque;    private Deque<Integer> secondDeque;    public CQueue() {        firstDeque = new LinkedList();        secondDeque = new LinkedList();    }    public void appendTail(int value) {        firstDeque.push(value);    }    public int deleteHead() {        if (secondDeque.isEmpty()) {            while (!firstDeque.isEmpty()) {                secondDeque.push(firstDeque.pop());            }        }        if (secondDeque.isEmpty()) {            return -1;        } else {            return secondDeque.pop();        }    }}