package com.arithmetic.solution;import java.util.HashMap;import java.util.Map;/** * @author Zhang Yu * @date 2022/3/7 10:57 PM * 复制随机链表 */public class CopyRandomList {    Map<Node, Node> cachedNode = new HashMap<Node, Node>();    public Node copyRandomList(Node head) {        if (head == null){            return null;        }        if (!cachedNode.containsKey(head)){            Node newNode = new Node(head.val);            cachedNode.put(head, newNode);            newNode.next = copyRandomList(head.next);            newNode.random = copyRandomList(head.random);        }        return cachedNode.get(head);    }}