package com.arithmetic.solution;/** * @author Zhang Yu * @date 2022/3/4 12:17 AM * 斐波那契数列 */public class Fibonacci {    public int fib(int n) {        final int MOD = 1000000007;        if (n < 2) {            return n;        }        int p = 0;        int q = 0;        int r = 1;        for (int i = 2; i <= n; ++i) {            p = q;            q = r;            r = (p + q) % MOD;        }        return r;    }}