package com.arithmetic.solution;import java.util.HashMap;import java.util.Map;/** * @author Zhang Yu * @date 2022/3/7 11:13 PM * 数组中重复的数字 */public class FindRepeatNumber {    public int findRepeatNumber(int[] nums) {        Map<Integer, Integer> map = new HashMap<>();        for (int i = 0; i < nums.length; i++) {            int num = nums[i];            Integer count = map.get(num);            if (count == null){                map.put(num, 1);            }else {                return num;            }        }        return 0;    }}