package com.arithmetic.solution;import java.util.Stack;/** * @author Zhang Yu * @date 2022/3/6 10:32 PM */public class MinStack {    private Stack<Integer> stack1;    private Stack<Integer> stack2;    public MinStack() {        stack1 = new Stack<>();        stack2 = new Stack<>();    }    public void push(int x) {        stack1.push(x);        if (stack2.isEmpty() || stack2.peek() >= x) {            stack2.push(x);        }    }    public void pop() {        if (stack1.pop().equals(stack2.peek())) {            stack2.pop();        }    }    public int top() {        return stack1.peek();    }    public int min() {        return stack2.peek();    }}