package com.arithmetic.solution;/** * @author Zhang Yu * @date 2022/3/7 11:03 PM * 替换空格 */public class ReplaceSpace {    public String replaceSpace(String s) {        StringBuilder result = new StringBuilder();        for (int i = 0; i < s.length(); i++) {            char charAt = s.charAt(i);            if (charAt == ' ') {                result.append("%20");            } else {                result.append(s.charAt(i));            }        }        return result.toString();    }}