package com.arithmetic.solution;/** * @author Zhang Yu * @date 2022/3/7 11:10 PM * 左旋转字符串 */public class ReverseLeftWords {    public String reverseLeftWords(String s, int n) {        StringBuilder left = new StringBuilder();        StringBuilder right = new StringBuilder();        for (int i = 0; i < s.length(); i++) {            if (i < n) {                left.append(s.charAt(i));            } else {                right.append(s.charAt(i));            }        }        right.append(left);        return right.toString();    }}